def findstartofpacket(input):
    startofpacket = ""
    count = 0
    for character in input:
        count += 1
        if len(startofpacket) == 4:
            if len(set(startofpacket)) == 4:
                result = input[:(count - 1)]
                return len(result)
            startofpacket = startofpacket[1:]
        if len(startofpacket) < 4:
            startofpacket = startofpacket + character

